<?php


namespace App\Traits;

use DOMWrap\Document;
use DOMWrap\NodeList;
use GuzzleHttp\Client;

trait AnnounceTrait
{
    private $announceJsonDir = '';
    private $remoteUrl = 'https://api-sinoalice.pokelabo.jp/web/announce';
    private $options = [
        'http' => [
            'method' => 'GET',
            'header' => 'User-Agent: iOS',
        ]
    ];
    private $replaceImage = [
            'category_icon01' => 'data:image/png;base64,',
            'category_icon02' => 'data:image/png;base64,',
            'category_icon03' => 'data:image/png;base64,',
            'category_icon04' => 'data:image/png;base64,',
            'heading_notes' => 'data:image/png;base64,',
            'arrow01' => 'data:image/png;base64,',
        ];

    /**
     * JSON
     * @param null $searchWord
     * @return string
     */
    public function getLocalList($searchWord = null)
    {
        $announceList = json_decode(file_get_contents($this->announceJsonDir.'index.json'));

        $data = array_filter($announceList, function ($announce) use ($searchWord) {
            if (!$searchWord) {
                return true;
            }

            $html = $this->getLocalDetail($announce->id);
            if (strpos($html, $searchWord) !== false) {
                return true;
            }
        });

        $array_column = array_column($data, 'date');
        array_multisort($array_column,
            SORT_DESC,
            $data);
        return $data;
    }

    /**
     * 
     * @param bool $refresh
     * @return array
     * @throws \GuzzleHttp\Exception\GuzzleException
     */
    public function getRemoteList($refresh = false)
    {
        $node = $this->getRemoteHtml($this->remoteUrl.'/index?type=0');

        $data = [];
        foreach($node->find('div#announce_list')->children() as $element)
        {
            $obj = new \stdClass();
            $obj->date  = $element->find('.frame_top_1 p')->text();
            $obj->title = $element->find('.announce_text')->text();

            $html = $element->html();
            $html = str_replace('https://webcontents-sinoalice.pokelabo.jp/web/img/alice/common/category_icon01.png', $this->replaceImage['category_icon01'], $html);
            $html = str_replace('https://webcontents-sinoalice.pokelabo.jp/web/img/alice/common/category_icon02.png', $this->replaceImage['category_icon02'], $html);
            $html = str_replace('https://webcontents-sinoalice.pokelabo.jp/web/img/alice/common/category_icon03.png', $this->replaceImage['category_icon03'], $html);
            $html = str_replace('https://webcontents-sinoalice.pokelabo.jp/web/img/alice/common/category_icon04.png', $this->replaceImage['category_icon04'], $html);
            $html = str_replace('<img src="https://webcontents-sinoalice.pokelabo.jp/web/img/alice/common/new.png" class="icon_new" width="35">', '', $html);
            $obj->html = $html;

            $urlQuery = parse_url('https://api-sinoalice.pokelabo.jp'.$element->attr('href'), PHP_URL_QUERY);
            $param = explode('=', $urlQuery);
            $obj->id = end($param);

            $data[] = $obj;
        }

        if ($refresh) {
            file_put_contents($this->announceJsonDir.'index.json', json_encode($data));
        }

        return $data;
    }

    /**
     * JSONbodyHTML
     * @param int $id
     * @return string
     */
    public function getLocalDetail($id)
    {
        $html = null;
        if (file_exists($this->announceJsonDir.$id.'.json')) {
            $html = json_decode(file_get_contents($this->announceJsonDir.$id.'.json'), true);
            $html = $html['current'];
        } else {
            $html = $this->getRemoteDetail($id, true);
        }

        return $html;
    }

    /**
     * @param $id
     * @param bool $refresh
     * @return string|string[]
     * @throws \GuzzleHttp\Exception\GuzzleException
     */
    private function getRemoteDetail($id, $refresh = false)
    {
        $html = $this->getRemoteHtml($this->remoteUrl.'/body?announceMstId='.$id)->find('body')->html();
        $html = str_replace('/web/announce/body?announceMstId=', '/announce/detail/', $html);
        $html = str_replace('/img/alice/common/heading_notes.png', $this->replaceImage['heading_notes'], $html);
        $html = str_replace('/img/alice/common/arrow01.png', $this->replaceImage['arrow01'], $html);

        if ($refresh) {
            file_put_contents($this->announceJsonDir.$id.'.json', json_encode(['current' => $html]));
        }
        return $html;
    }

    /**
     * 
     */
    public function checkDiff()
    {
        $localAnnounce = $this->getLocalList();
        $remoteAnnounce = $this->getRemoteList(true);

        // 
        $remote = [];
        $local  = [];
        $update = [];

        foreach ($localAnnounce as $localKey => $localValue)
        {
            foreach ($remoteAnnounce as $remoteKey => $remoteValue)
            {
                // 
                if ($localValue->id === $remoteValue->id)
                {
                    $remote[] = $remoteKey;
                    $local[]  = $localKey;

                    // 
                    $localDetail  = $this->getLocalDetail($localValue->id);
                    $remoteDetail = $this->getRemoteDetail($remoteValue->id);
                    if ($localDetail !== $remoteDetail)
                    {
                        $update[] = $remoteKey;
                        file_put_contents($this->announceJsonDir.$remoteValue->id.'.json', json_encode(['current' => $remoteDetail]));
                    }

                    break;
                }
            }
        }

        // 
        $newAnnounce = array_filter($remoteAnnounce, function ($k) use ($remote) {
            return !in_array($k, $remote, true);
        }, ARRAY_FILTER_USE_KEY);

        // 
        $delAnnounce = array_filter($localAnnounce, function ($k) use ($local) {
            return !in_array($k, $local, true);
        }, ARRAY_FILTER_USE_KEY);

        // 
        $updateAnnounce = array_filter($remoteAnnounce, function ($k) use ($update) {
            return in_array($k, $update, true);
        }, ARRAY_FILTER_USE_KEY);

        return ['new' => $newAnnounce, 'del' => $delAnnounce, 'update' => $updateAnnounce];
    }

    /**
     * URLHTMLbody
     * @param $url
     * @return NodeList|string
     * @throws \GuzzleHttp\Exception\GuzzleException
     */
    private function getRemoteHtml($url)
    {
        $client = new Client;
        $response = $client->get($url);
        $html = (string) $response->getBody();
        return (new Document())->html($html);
    }


}
